sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }


	var aja= new GlideAjax('catalog');
	aja.addParam('sysparm_name','cat');
	aja.addParam('sysparm_class_name',newValue);
	aja.getXML(callBackFunction);

	function callBackFunction(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		if(answer=="false"){
			//g_form.setValue('description',g_form.getValue('cmdb_ci'));
		}
		else{
 		var res=confirm('this configuration item already exist in another record, dou you want continue?',answer);
          if(res==true){
 			  //nada
 		  }
		else{
 			g_form.clearValue('cmdb_ci');
		}
		}
	}

}
