sys_script_include.script:
var location = Class.create();
location.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	location: function(){
		var test='';
		var res='';
	var gr = new GlideRecord('sys_user');
		gr.addQuery('sys_id',this.getParameter('sysparm_location'));
		gr.query();

		if(gr.next()){
			res=gr.location;
				}		

		//return res;

		var gr2= new GlideRecord('cmdb_ci');
			gr2.addQuery('assigned_to',this.getParameter('sysparm_location'));
			gr2.query();
			if(gr2.next()){
				test=gr2.sys_id.toString();
				}
		return test+','+res;
},
	type: 'location'
});
