sys_script_include.script:
var catalog = Class.create();
catalog.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	cat: function(){
		var res="false";
		var CI = this.getParameter('sysparm_class_name');
	var gr= new GlideRecord('task');
		gr.addQuery('cmdb_ci', CI);
		gr.query();

		if(gr.next()){
			var gr2 = new GlideRecord("cmdb_ci");
			gr2.addQuery("sys_id", CI  );
			gr2.query();
			if(gr2.next()){
				res = gr2.name;	
			}
		}
		//gs.addInfoMessage(res);
	return res;

	},


	type: 'catalog'
});
